    private void jButtonConsultarEmpleadoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonConsultarEmpleadoActionPerformed
        // TODO add your handling code here:
        
        empleado = mdEmpleado.consultaEmpleado(jTextFieldCodigo.getText());
        
        if (empleado == null) {
            JOptionPane.showMessageDialog(rootPane, "Empleado no existe");
            
            jTextFieldNombre.setText("");
            jTextFieldApellido.setText("");
            jTextFieldSalario.setText("");
            jTextValorHoraExtra.setText("");
            jTextFieldCodigo.setText("");
            jCheckBoxAuxTransporte.setSelected(false);
            
            jTextField1Nomina.setText("0");
            jTextFieldPrestaciones.setText("0"); 
        } else {
            jTextFieldNombre.setText(empleado.getNombre());
            jTextFieldApellido.setText(empleado.getApellido());
            jTextFieldSalario.setText(String.valueOf(empleado.getSalario()));
            jTextValorHoraExtra.setText(String.valueOf(empleado.getHorasExtra()));
            jCheckBoxAuxTransporte.setSelected(empleado.isAuxilioTransporte());
        }        
    }//GEN-LAST:event_jButtonConsultarEmpleadoActionPerformed

