    private void jButtonGenerarNominaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonGenerarNominaActionPerformed
        // TODO add your handling code here:
        
        
        ArrayList<Double> nomina = new ArrayList<>();
        ArrayList<Double> prestacion = new ArrayList<>();
        empleado = mdEmpleado.consultaEmpleado(jTextFieldCodigo.getText());
        
        System.out.println("Liquidacion de Nominas: ");
        nomina = liquidacion.liquidarNominaEmp(empleado);
        
        for (int i = 0; i < nomina.size(); i++) {
            System.out.println(" Total a pagar: " + Math.round(nomina.get(i)));
            jTextField1Nomina.setText(String.valueOf(Math.round(nomina.get(i))));
            
        }
        System.out.println("Prestaciones Sociales");
        prestacion = liquidacion.liquidarPrestacionesEmp(empleado);
        
        for (int i = 0; i < prestacion.size(); i++) {
            System.out.format(" Total a pagar: %.2f", prestacion.get(i));
            jTextFieldPrestaciones.setText(String.valueOf(Math.round(prestacion.get(i))));
        } 
    }//GEN-LAST:event_jButtonGenerarNominaActionPerformed

