    private void jButtonGuardarEmpleadoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonGuardarEmpleadoActionPerformed
        // TODO add your handling code here:
        
        
        
        if(jTextFieldNombre1.getText().equals("") || jTextFieldApellido1.getText().equals("") || Integer.valueOf(jTextFieldSalario1.getText()) <= 0){
            
            JOptionPane.showMessageDialog(rootPane, "Los datos del nombre, apellido "
                                                    + "y salario son obligatorios");
        }else{
            if(mdEmpleado.crearEmpleado(jTextFieldNombre1.getText(), jTextFieldApellido1.getText(), Integer.valueOf(jTextValorHoraExtra1.getText()), jCheckBoxAuxTransporte1.isSelected(), Integer.valueOf(jTextFieldSalario1.getText()))){
                JOptionPane.showMessageDialog(rootPane, "Empleado guardado correctamente: \n" + jTextFieldNombre1.getText() + " " + jTextFieldApellido1.getText());
            }else{
                JOptionPane.showMessageDialog(rootPane, "Empleado NO guardado, intente nuevamente: \n" + jTextFieldNombre1.getText() + " " + jTextFieldApellido1.getText());
            }
        }
    }//GEN-LAST:event_jButtonGuardarEmpleadoActionPerformed

